import React from 'react';
import { useState } from 'react';

const Navbar = ({ logo }) => {
  const [showAbout, setShowAbout] = useState(false);
  
  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-logo">
          <img src={logo} alt="JEE Explorer Logo" className="logo-image" />
          <span className="logo-text">JEE Explorer</span>
        </div>
        <div className="navbar-links">
          <button 
            className="about-btn" 
            onClick={() => setShowAbout(!showAbout)}
          >
            About
          </button>
        </div>
      </div>
      
      {showAbout && (
        <div className="about-modal">
          <div className="about-content">
            <h2>About JEE Explorer</h2>
            <p>JEE Explorer helps engineering aspirants find eligible colleges based on their JEE Main/Advanced ranks, category, and preferences.</p>
            <p>Use this tool to explore IITs, NITs, and IIITs with your specific rank and preferences to make informed decisions about your engineering education.</p>
            <button 
              className="close-about-btn"
              onClick={() => setShowAbout(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
